import React from "react";
import ProjectCard from "../../components/ProjectCard";
import { Status } from "../../components/Status";
import DbService from "../../Services/DbServices";

interface ProjectsState {
	items: JSX.Element[] | null;
	isLoading: boolean;
	hasFailed: boolean;
}

class ProjectsIndex extends React.Component<any, ProjectsState> {
	state: ProjectsState = { items: [], isLoading: true, hasFailed: false };

	componentDidMount() {
		DbService.getProjects()
			.then((data) => {
				const items = new Array<JSX.Element>();
				data.forEach((project) => {
					items.push(
						<ProjectCard
							name={project.name}
							language={project.language}
							url={project.url}
							license={project.license}
							description={project.description}
							key={project.name}
						/>
					);
				});
				this.setState({ items, isLoading: false });
			})
			.catch((_: any) => {
				this.setState({
					items: [],
					isLoading: false,
					hasFailed: true,
				});
			});
	}

	render() {
		if (this.state.hasFailed) {
			return <Status>Can't connect to database</Status>;
		}
		if (this.state.isLoading) {
			return <Status>Loading...</Status>;
		}
		return <React.Fragment>{this.state?.items}</React.Fragment>;
	}
}
export default ProjectsIndex;

export async function getStaticProps(context) {
	return {
		props: {},
	};
}
